mapper.select:
select b.SYS_DT as sysDt , b.SYS_USER as sysUser , 'test' as sysUsernm , b.UPD_DT as updDt , b.UPD_USER as updUser , b.PK_ARTICLE as pkArticle , b.PPK_ARTICLE as ppkArticle , b.TITLE as title , b.CONTENTS as contents , b.PWD as pwd , b.HIT_CNT as hitCnt , b.FROM_DT as fromDt , b.TO_DT as toDt , b.seq as seq , 'Y' as checkYn , case when d.PK_FILE is not null then 'Y' else 'N' end as fileYn , (select group_concat(z.PK_FILE,'|',z.FILE_NM,'|',z.size order by z.PK_FILE SEPARATOR ',') from grarticle_files x inner join cmfileinfo z on (x.PK_FILE = z.PK_FILE) where x.PK_ARTICLE = b.PK_ARTICLE ) as fileInfo -- uuid | path | name | size , b.FK_BOARD as fkBoard from grboard a inner join grarticle b on (a.PK_BOARD = b.FK_BOARD) left outer join grarticle_files c on (b.PK_ARTICLE = c.PK_ARTICLE) left outer join cmfileinfo d on (c.pk_file = d.PK_FILE) where a.PK_BOARD = #{pkBoard}
and b.title like #{title}
and b.contents like #{contents}
order by b.PK_ARTICLE desc
